def hamming_distance(s, t):
    # Ensure the strings are of equal length
    assert len(s) == len(t), "Strings must be of equal length"
    
    # Count mismatches
    return sum(1 for a, b in zip(s, t) if a != b)

# Example usage
s = ""
t = ""
print(hamming_distance(s, t))  # Output should be 7